import React, {useState} from 'react';
import API from '../api';

export default function ProfileSettings({ notificationsEnabled = true, onNotificationChange }){
  const [allow, setAllow] = useState(notificationsEnabled);

  async function toggle(){
    if (!allow) {
      const p = await Notification.requestPermission();
      if (p !== 'granted') return alert('Allow notifications in your browser settings');
    }
    setAllow(!allow);
    if (onNotificationChange) onNotificationChange(!allow);
    alert('Settings saved (client-side demo)');
  }

  return (
    <div className="card">
      <h3>Profile Settings</h3>
      <label>
        <input type="checkbox" checked={allow} onChange={toggle}/>
        Enable Notifications (popup for tweets with cricket/science)
      </label>
    </div>
  );
}
