import React, { useState } from "react";
import API from "../api";

export default function PostComposer({ onPosted }) {
  const [text, setText] = useState("");
  const [caption, setCaption] = useState("");
  const [media, setMedia] = useState(null);
  const [mediaPreview, setMediaPreview] = useState(null);
  const [loading, setLoading] = useState(false);

  function handleMediaChange(e) {
    const file = e.target.files[0];
    if (file) {
      setMedia(file);
      setMediaPreview(URL.createObjectURL(file));
    } else {
      setMedia(null);
      setMediaPreview(null);
    }
  }

  async function post() {
    setLoading(true);
    try {
      const fd = new FormData();
      fd.append("text", text);
      fd.append("caption", caption);
      if (media) fd.append("media", media);
      const res = await API.post("/tweets", fd, { headers: { "Content-Type": "multipart/form-data" } });
      const { notify } = res.data;
      if (notify && Notification.permission === "granted") {
        new Notification("Highlighted tweet", { body: text });
      }
      setText("");
      setCaption("");
      setMedia(null);
      setMediaPreview(null);
      onPosted && onPosted();
    } catch (err) {
      alert(err.response?.data?.message || err.message);
    }
    setLoading(false);
  }

  return (
    <div className="composer bg-white rounded-xl shadow-sm p-4 flex flex-col gap-4">
      <textarea
        value={text}
        onChange={e => setText(e.target.value)}
        placeholder={"What's happening?"}
        className="w-full rounded-2xl border border-gray-300 p-3 text-base focus:outline-none focus:ring-2 focus:ring-blue-300 resize-none mb-2"
        rows={3}
      ></textarea>
      <input
        type="text"
        value={caption}
        onChange={e => setCaption(e.target.value)}
        placeholder="Add a caption (optional)"
        className="w-full rounded-2xl border border-gray-200 p-3 text-base focus:outline-none focus:ring-2 focus:ring-blue-300 mb-2"
      />
      <div className="flex flex-col gap-2">
        <label className="block font-medium text-blue-700 mb-1">Upload Photo/Video</label>
        <input type="file" accept="image/*,video/*" onChange={handleMediaChange} className="block" />
        {mediaPreview && (
          <div className="mt-2">
            {media && media.type.startsWith("image") ? (
              <img src={mediaPreview} alt="preview" className="max-h-48 rounded-lg shadow border border-gray-200" />
            ) : (
              <video src={mediaPreview} controls className="max-h-48 rounded-lg shadow border border-gray-200" />
            )}
          </div>
        )}
      </div>
      <button
        onClick={post}
        disabled={loading || (!text.trim() && !media)}
        className="self-end px-6 py-2 rounded-full bg-gradient-to-r from-blue-500 to-cyan-400 text-white font-bold shadow hover:scale-105 transition flex items-center gap-2"
      >
        {loading && (
          <svg className="animate-spin h-5 w-5 text-white" viewBox="0 0 24 24">
            <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" fill="none" />
            <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8v4l3-3-3-3v4a8 8 0 01-8 8z" />
          </svg>
        )}
        Tweet
      </button>
    </div>
  );
}
