import React, { useState, useEffect } from "react";
import ProfileSettings from "../components/ProfileSettings";
import API from "../api";

export default function Profile() {
  // Placeholder user data
  const user = {
    username: "twiller_user",
    email: "user@example.com",
    joinDate: "Jan 2024",
    avatar: "https://ui-avatars.com/api/?name=Twiller+User&background=0D8ABC&color=fff"
  };
  const tweets = [
    { id: 1, text: "Hello Twiller!", date: "Sep 2025" },
    { id: 2, text: "My first audio tweet!", date: "Sep 2025" }
  ];
  const [notificationsEnabled, setNotificationsEnabled] = useState(true);

  useEffect(() => {
    // Fetch user notification preference from backend
    API.get("/auth/me").then(res => {
      setNotificationsEnabled(res.data.notificationsEnabled ?? true);
    }).catch(()=>{});
  }, []);

  async function handleNotificationChange(enabled) {
    setNotificationsEnabled(enabled);
    await API.post("/auth/notifications", { enabled });
  }

  return (
    <div className="w-full max-w-2xl mx-auto py-6">
      <ProfileSettings notificationsEnabled={notificationsEnabled} onNotificationChange={handleNotificationChange} />
      <div className="bg-white rounded-xl shadow p-6 mb-6 flex flex-col items-center">
        <img src={user.avatar} alt="avatar" className="w-24 h-24 rounded-full shadow mb-3" />
        <button className="mb-2 px-4 py-1 rounded-full bg-gradient-to-r from-blue-500 to-cyan-400 text-white font-bold shadow hover:scale-105 transition">Upload Photo</button>
        <div className="text-lg font-bold text-blue-700">{user.username}</div>
        <div className="text-gray-500">{user.email}</div>
        <div className="text-gray-400 text-sm">Joined {user.joinDate}</div>
      </div>
      <div className="bg-white rounded-xl shadow p-6">
        <h2 className="font-bold text-lg mb-4 text-blue-700">Your Tweets</h2>
        <ul className="flex flex-col gap-3">
          {tweets.map(t => (
            <li key={t.id} className="bg-gray-50 px-4 py-2 rounded-lg shadow border border-gray-100">
              <div className="font-medium">{t.text}</div>
              <div className="text-gray-400 text-xs">{t.date}</div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
