export default function AudioRecorder({ onUploaded }) {
  const [recording, setRecording] = useState(false);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [chunks, setChunks] = useState([]);
  const [otpSent, setOtpSent] = useState(false);
  const [otp, setOtp] = useState('');
  const [otpVerified, setOtpVerified] = useState(false);
  const [duration, setDuration] = useState(0);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const audioRef = useRef();

  async function startRecording(){
    const now = new Date();
    const istNow = new Date(now.toLocaleString('en-US', { timeZone: 'Asia/Kolkata' }));
    const hour = istNow.getHours();
    if (hour < 14 || hour >= 19) return alert('Audio uploads allowed only 2PM-7PM IST');

    const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
    const mr = new MediaRecorder(stream);
    const localChunks = [];
    mr.ondataavailable = e => localChunks.push(e.data);
    mr.onstop = ()=> setChunks(localChunks);
    mr.start();
    setMediaRecorder(mr);
    setRecording(true);

    setTimeout(() => {
      if (mr.state === 'recording') mr.stop();
      setRecording(false);
    }, 300000);
  }

  function stopRecording(){
    if (mediaRecorder && mediaRecorder.state === 'recording') mediaRecorder.stop();
    setRecording(false);
  }

  function play(){
    if (!chunks.length) return;
    const blob = new Blob(chunks, { type: 'audio/webm' });
    const url = URL.createObjectURL(blob);
    audioRef.current.src = url;
    audioRef.current.onloadedmetadata = () => {
      setDuration(audioRef.current.duration);
    };
    audioRef.current.play();
  }

  async function sendOtp(){
    setError('');
    setLoading(true);
    try {
      await API.post('/audio/send-otp');
      setOtpSent(true);
      setError('OTP sent to your email.');
    } catch (err) {
      setError(err.response?.data?.message || err.message);
    }
    setLoading(false);
  }

  async function verifyOtp(){
    setError('');
    setLoading(true);
    try {
      await API.post('/audio/verify-otp', { code: otp });
      setOtpVerified(true);
      setError('OTP verified, now you can upload audio.');
    } catch (err) {
      setError(err.response?.data?.message || err.message);
    }
    setLoading(false);
  }

  async function upload(){
    setError('');
    if (!chunks.length) return setError('No audio recorded');
    const blob = new Blob(chunks, { type: 'audio/webm' });
    if (blob.size > 100 * 1024 * 1024) return setError('File too big');
    if (duration > 300) return setError('Audio is longer than 5 minutes');
    if (!otpVerified) return setError('Please verify OTP before uploading');
    setLoading(true);
    try {
      const fd = new FormData();
      fd.append('audio', blob, 'audio.webm');
      await API.post('/audio/upload', fd, { headers: { 'Content-Type': 'multipart/form-data' }});
      setError('Uploaded successfully!');
      setChunks([]);
      setOtpVerified(false);
      setOtpSent(false);
      setOtp('');
      onUploaded && onUploaded();
    } catch (err) {
      setError(err.response?.data?.message || err.message);
    }
    setLoading(false);
  }

  return (
    <div className="card">
      <h4>Audio Tweet</h4>
      {error && <div style={{color: error.includes('success') ? '#388e3c' : '#d32f2f', marginBottom:8, fontWeight:500}}>{error}</div>}
      <button onClick={recording ? stopRecording : startRecording} disabled={loading}>
        {recording ? 'Stop' : 'Record (max 5 min)'}
      </button>
      <button onClick={play} disabled={loading}>Play</button>
      <audio ref={audioRef} controls />
      {duration > 0 && <div>Duration: {Math.floor(duration/60)}:{('0'+Math.floor(duration%60)).slice(-2)} min</div>}
      <div>
        {!otpSent && <button onClick={sendOtp} disabled={loading}>Send OTP to verify</button>}
        {otpSent && !otpVerified && <>
          <input value={otp} onChange={e=>setOtp(e.target.value)} placeholder="Enter OTP" disabled={loading}/>
          <button onClick={verifyOtp} disabled={loading}>Verify OTP</button>
        </>}
        {otpVerified && <span style={{color:'green'}}>OTP Verified!</span>}
      </div>
      <button onClick={upload} disabled={!otpVerified || loading}>Upload</button>
      {loading && <div style={{color:'#1d9bf0', marginTop:8}}>Processing...</div>}
    </div>
  );
}
