import React, { useEffect, useState } from "react";
import API from "./api";
import Sidebar from "./layout/Sidebar";
import Feed from "./layout/Feed";
import RightPanel from "./layout/RightPanel";
import AuthForm from "./components/AuthForm";


export default function App() {
  const [tweets, setTweets] = useState([]);
  const [notificationsEnabled] = useState(() => {
    const saved = localStorage.getItem("notificationsEnabled");
    return saved === null ? true : saved === "true";
  });
  const [user, setUser] = useState(() => {
    const u = localStorage.getItem("user");
    return u ? JSON.parse(u) : null;
  });

  // Google OAuth: capture JWT from URL and store in localStorage

  // On mount: check for token in URL or localStorage, fetch user info
  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    let token = params.get("token");
    if (token) {
      localStorage.setItem("token", token);
      window.history.replaceState({}, document.title, window.location.pathname);
    } else {
      token = localStorage.getItem("token");
    }
    if (token && !user) {
      fetchUserFromToken(token);
    }
  }, []);

  async function fetchUserFromToken(token) {
    try {
      // Always get token from localStorage if not provided
      if (!token) {
        token = localStorage.getItem("token");
      }
      console.log("Sending token to /auth/me:", token);
      if (!token) {
        console.warn("No token found in localStorage. User not authenticated.");
        setUser(null);
        localStorage.removeItem("user");
        localStorage.removeItem("token");
        return;
      }
      const res = await API.get("/auth/me", {
        headers: { Authorization: `Bearer ${token}` }
      });
      if (res.data && res.data.user) {
        setUser(res.data.user);
        localStorage.setItem("user", JSON.stringify(res.data.user));
      }
    } catch (err) {
      console.error("/auth/me 401 error. Token used:", token, err);
      setUser(null);
      localStorage.removeItem("user");
      localStorage.removeItem("token");
    }
  }

  useEffect(() => {
    if (user) fetchTweets();
  }, [user]);


  async function fetchTweets() {
  const res = await API.get("/tweets");
  setTweets(res.data);
  }

  useEffect(() => {
    if (!notificationsEnabled || !("Notification" in window)) return;
    tweets.forEach(t => {
      if (!t._notified && t.text && /(cricket|science)/i.test(t.text)) {
        if (Notification.permission === "granted") {
          new Notification("New Tweet", { body: t.text });
          t._notified = true;
        }
      }
    });
  }, [tweets, notificationsEnabled]);

  function handleAuth(u) {
  setUser(u);
  localStorage.setItem("user", JSON.stringify(u));
  }

  function handleLogout() {
  setUser(null);
  localStorage.removeItem("user");
  localStorage.removeItem("token");
  }

  if (!user) {
    return <AuthForm onAuth={handleAuth} />;
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-teal-50 font-sans">
      <div className="flex w-full max-w-7xl mx-auto min-h-screen relative">
        {/* Sidebar: left, fixed width, hidden on mobile */}
        <div className="hidden md:flex w-72 flex-shrink-0">
          <Sidebar user={user} onLogout={handleLogout} />
        </div>
        {/* Feed: center, wider, border-x, full width on mobile */}
        <main className="flex-1 bg-white/80 px-0 md:px-8 py-0 min-h-screen border-x border-gray-200 md:border-x-0">
          <Feed tweets={tweets} fetchTweets={fetchTweets} />
        </main>
        {/* RightPanel: right, hidden on mobile/tablet */}
        <div className="hidden lg:flex w-80 flex-shrink-0">
          <RightPanel />
        </div>
        {/* Mobile Bottom Nav */}
        <div className="md:hidden fixed bottom-0 left-0 w-full bg-white/90 border-t border-gray-200 flex justify-around items-center h-14 z-50 shadow-lg">
          <Sidebar user={user} onLogout={handleLogout} mobile />
        </div>
      </div>
    </div>
  );
}
