import React, { useState } from 'react';
import API from '../api';

export default function PostComposer({ onPosted }){
  const [text, setText] = useState('');

  async function post(){
    try {
      const res = await API.post('/tweets', { text });
      const { notify } = res.data;
      if (notify && Notification.permission === 'granted') {
        new Notification('Highlighted tweet', { body: text });
      }
      setText('');
      onPosted && onPosted();
    } catch (err) {
      alert(err.response?.data?.message || err.message);
    }
  }

  return (
    <div className="composer card">
      <textarea value={text} onChange={e=>setText(e.target.value)} placeholder="What's happening?"></textarea>
      <button onClick={post}>Tweet</button>
    </div>
  );
}
