import React from "react";
import { Link, useLocation } from "react-router-dom";

const navItems = [
  { icon: "üè†", label: "Home", to: "/" },
  { icon: "üîç", label: "Explore", to: "/explore" },
  { icon: "üîî", label: "Notifications", to: "/notifications" },
  { icon: "‚úâÔ∏è", label: "Messages", to: "/messages" },
  { icon: "üë§", label: "Profile", to: "/profile" },
];

export default function Sidebar({ user, onLogout, mobile }) {
  const location = useLocation();
  if (mobile) {
    return (
      <nav className="mobile-nav">
        {navItems.map((item) => (
          <Link key={item.label} to={item.to} className={`mobile-nav-link ${location.pathname === item.to ? "active" : ""}`}>
            <span className="mobile-icon">{item.icon}</span>
          </Link>
        ))}
        <button onClick={onLogout} className="mobile-nav-link">üö™</button>
      </nav>
    );
  }

  return (
    <aside className="sidebar">
      <div className="brand">Twiller</div>
      <nav className="nav-list">
        {navItems.map((item) => (
          <SidebarLink key={item.label} icon={item.icon} label={item.label} to={item.to} active={location.pathname === item.to} />
        ))}
      </nav>
      <div className="sidebar-bottom">
        <div className="profile-row sidebar-profile">
          <img className="w-12 h-12 rounded-full bg-gray-200" src={user?.avatar || "https://abs.twimg.com/sticky/default_profile_images/default_profile_400x400.png"} alt="avatar" />
          <div className="profile-meta">
            <div className="font-bold text-md">{user?.name}</div>
            <div className="text-xs text-gray-500">@{user?.email?.split("@")[0]}</div>
          </div>
          <button onClick={onLogout} className="logout-icon" title="Logout">üö™</button>
        </div>
      </div>
    </aside>
  );
}

function SidebarLink({ icon, label, to, active }) {
  return (
    <Link to={to} className={`sidebar-link ${active ? "active" : ""}`}>
      <span className="link-icon">{icon}</span>
      <span className="link-label">{label}</span>
    </Link>
  );
}
