import React, { useEffect, useState } from "react";
import API from "./api";
import Sidebar from "./layout/Sidebar";
import Feed from "./layout/Feed";
import RightPanel from "./layout/RightPanel";
import AuthForm from "./components/AuthForm";


export default function App() {
  const [tweets, setTweets] = useState([]);
  const [notificationsEnabled, setNotificationsEnabled] = useState(() => {
    const saved = localStorage.getItem("notificationsEnabled");
    return saved === null ? true : saved === "true";
  });
  const [user, setUser] = useState(() => {
    const u = localStorage.getItem("user");
    return u ? JSON.parse(u) : null;
  });

  // Google OAuth: capture JWT from URL and store in localStorage

  // On mount: check for token in URL or localStorage, fetch user info
  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    let token = params.get("token");
    if (token) {
      localStorage.setItem("token", token);
      window.history.replaceState({}, document.title, window.location.pathname);
    } else {
      token = localStorage.getItem("token");
    }
    if (token && !user) {
      fetchUserFromToken(token);
    }
  }, []);

  async function fetchUserFromToken(token) {
    try {
      const res = await fetch("http://localhost:5000/api/auth/me", {
        headers: { Authorization: `Bearer ${token}` }
      });
      if (res.ok) {
        const data = await res.json();
        if (data && data.user) {
          setUser(data.user);
          localStorage.setItem("user", JSON.stringify(data.user));
        }
      }
    } catch (err) {
      // handle error
    }
  }

  useEffect(() => {
  if (user) fetchTweets();
  }, [user]);


  async function fetchTweets() {
  const res = await API.get("/tweets");
  setTweets(res.data);
  }

  useEffect(() => {
    if (!notificationsEnabled || !("Notification" in window)) return;
    tweets.forEach(t => {
      if (!t._notified && t.text && /(cricket|science)/i.test(t.text)) {
        if (Notification.permission === "granted") {
          new Notification("New Tweet", { body: t.text });
          t._notified = true;
        }
      }
    });
  }, [tweets, notificationsEnabled]);

  function handleAuth(u) {
  setUser(u);
  localStorage.setItem("user", JSON.stringify(u));
  }

  function handleLogout() {
  setUser(null);
  localStorage.removeItem("user");
  localStorage.removeItem("token");
  }

  if (!user) {
    return <AuthForm onAuth={handleAuth} />;
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-teal-50 font-sans">
      <div className="flex w-full max-w-7xl mx-auto min-h-screen">
        <Sidebar user={user} onLogout={handleLogout} />
        <Feed tweets={tweets} fetchTweets={fetchTweets} />
        <RightPanel />
      </div>
    </div>
  );
}
