import React, { useEffect, useState } from "react";
import API from "./api";
import PostComposer from "./components/PostComposer";
import ProfileSettings from "./components/ProfileSettings";
import AudioRecorder from "./components/AudioRecorder";
import SubscriptionPage from "./components/SubscriptionPage";
import CustomVideoPlayer from "./components/CustomVideoPlayer";
import ForgotPassword from "./components/ForgotPassword";
import AuthForm from "./components/AuthForm";


export default function App() {
  const [tweets, setTweets] = useState([]);
  const [notificationsEnabled, setNotificationsEnabled] = useState(() => {
    const saved = localStorage.getItem("notificationsEnabled");
    return saved === null ? true : saved === "true";
  });
  const [user, setUser] = useState(() => {
    const u = localStorage.getItem("user");
    return u ? JSON.parse(u) : null;
  });

  // Google OAuth: capture JWT from URL and store in localStorage

  // On mount: check for token in URL or localStorage, fetch user info
  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    let token = params.get("token");
    if (token) {
      localStorage.setItem("token", token);
      window.history.replaceState({}, document.title, window.location.pathname);
    } else {
      token = localStorage.getItem("token");
    }
    if (token && !user) {
      fetchUserFromToken(token);
    }
  }, []);

  async function fetchUserFromToken(token) {
    try {
      const res = await fetch("http://localhost:5000/api/auth/me", {
        headers: { Authorization: `Bearer ${token}` }
      });
      if (res.ok) {
        const data = await res.json();
        if (data && data.user) {
          setUser(data.user);
          localStorage.setItem("user", JSON.stringify(data.user));
        }
      }
    } catch (err) {
      // handle error
    }
  }

  useEffect(() => {
  if (user) fetchTweets();
  }, [user]);

  function handleNotificationSetting(val) {
  setNotificationsEnabled(val);
  localStorage.setItem("notificationsEnabled", val);
  }

  async function fetchTweets() {
  const res = await API.get("/tweets");
  setTweets(res.data);
  }

  useEffect(() => {
    if (!notificationsEnabled || !("Notification" in window)) return;
    tweets.forEach(t => {
      if (!t._notified && t.text && /(cricket|science)/i.test(t.text)) {
        if (Notification.permission === "granted") {
          new Notification("New Tweet", { body: t.text });
          t._notified = true;
        }
      }
    });
  }, [tweets, notificationsEnabled]);

  function handleAuth(u) {
  setUser(u);
  localStorage.setItem("user", JSON.stringify(u));
  }

  function handleLogout() {
  setUser(null);
  localStorage.removeItem("user");
  localStorage.removeItem("token");
  }

  if (!user) {
  return <AuthForm onAuth={handleAuth} />;
  }

  return (
    <>
  <div className="navbar">Twiller <button style={{float:"right",background:"none",border:"none",color:"#1d9bf0",cursor:"pointer"}} onClick={handleLogout}>Logout</button></div>
      <div className="app">
        <div className="sidebar">
          <div className="profile-section">
            <img className="profile-avatar" src={user.avatar || "https://abs.twimg.com/sticky/default_profile_images/default_profile_400x400.png"} alt="avatar" />
            <div>
              <div style={{fontWeight:"bold"}}>{user.name}</div>
              <div style={{color:"#536471", fontSize:"0.95em"}}>@{user.email?.split("@")[0]}</div>
            </div>
          </div>
          <ProfileSettings notificationsEnabled={notificationsEnabled} onNotificationChange={handleNotificationSetting} />
          <SubscriptionPage />
        </div>
        <main>
          <PostComposer onPosted={fetchTweets}/>
          <AudioRecorder onUploaded={fetchTweets}/>
          <ForgotPassword/>
          <div>
            {tweets.map(t => (
              <div key={t._id} className="tweet">
                <strong>{t.user?.name}</strong>
                <p>{t.text}</p>
                {t.audioUrl && <audio controls src={`http://localhost:5000${t.audioUrl}`} />}
                {t.videoUrl && <CustomVideoPlayer src={t.videoUrl} />}
              </div>
            ))}
          </div>
        </main>
      </div>
    </>
  );
}
