import React from "react";
import { Link, useLocation } from "react-router-dom";

const navItems = [
  { icon: "üè†", label: "Home", to: "/" },
  { icon: "üîç", label: "Explore", to: "/explore" },
  { icon: "üîî", label: "Notifications", to: "/notifications" },
  { icon: "‚úâÔ∏è", label: "Messages", to: "/messages" },
  { icon: "üë§", label: "Profile", to: "/profile" },
];

export default function Sidebar({ user, onLogout, mobile }) {
  const location = useLocation();
  if (mobile) {
    // Mobile bottom nav: icons only
    return (
      <nav className="flex w-full justify-around items-center">
        {navItems.map((item) => (
          <Link
            key={item.label}
            to={item.to}
            className={`flex flex-col items-center justify-center text-gray-700 hover:text-blue-500 transition ${location.pathname === item.to ? "text-blue-600 font-bold" : ""}`}
          >
            <span className="text-2xl">{item.icon}</span>
          </Link>
        ))}
        <button onClick={onLogout} className="flex flex-col items-center justify-center text-gray-700 hover:text-blue-500 transition">
          <span role="img" aria-label="logout" className="text-2xl">üö™</span>
        </button>
      </nav>
    );
  }
  // Desktop sidebar
  return (
    <aside className="flex flex-col h-full p-4 border-r border-gray-200 bg-white min-w-[220px]">
      <div>
        <div className="mb-8 flex items-center gap-2">
          <span className="text-3xl font-extrabold text-blue-500">Twiller</span>
        </div>
        <nav className="flex flex-col gap-2">
          {navItems.map((item) => (
            <SidebarLink key={item.label} icon={item.icon} label={item.label} to={item.to} active={location.pathname === item.to} />
          ))}
        </nav>
      </div>
      <div className="mt-8 flex flex-col gap-4">
        <div className="flex items-center gap-3 mb-2">
          <img className="w-12 h-12 rounded-full bg-gray-200" src={user?.avatar || "https://abs.twimg.com/sticky/default_profile_images/default_profile_400x400.png"} alt="avatar" />
          <div>
            <div className="font-bold text-lg">{user?.name}</div>
            <div className="text-xs text-gray-500">@{user?.email?.split("@")[0]}</div>
          </div>
        </div>
        <button onClick={onLogout} className="flex items-center gap-2 w-full mt-2 px-4 py-3 rounded-full text-blue-600 font-bold hover:bg-blue-50 transition justify-center">
          <span role="img" aria-label="logout" className="text-xl">üö™</span> Logout
        </button>
      </div>
    </aside>
  );
}

function SidebarLink({ icon, label, to, active }) {
  return (
    <Link
      to={to}
      className={`flex items-center gap-4 px-4 py-3 rounded-full text-lg transition ${active ? "bg-blue-50 text-blue-600 font-bold" : "text-gray-700 hover:bg-blue-50 hover:font-bold"}`}
    >
      <span className="text-2xl">{icon}</span>
      <span>{label}</span>
    </Link>
  );
}
