import React, { useState } from 'react';
import API from '../api';

export default function AuthForm({ onAuth }) {
  const [mode, setMode] = useState('login');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState('');
  const [loading, setLoading] = useState(false);

  async function handleSubmit(e) {
    e.preventDefault();
    setLoading(true);
    try {
      if (mode === 'login') {
        const res = await API.post('/auth/login', { email, password });
        localStorage.setItem('token', res.data.token);
        onAuth(res.data.user);
      } else {
        const res = await API.post('/auth/register', { name, email, password });
        alert('Registration successful! Please log in.');
        setMode('login');
      }
    } catch (err) {
      alert(err.response?.data?.message || err.message);
    }
    setLoading(false);
  }

  return (
    <div className="auth-form card">
      <h2 style={{textAlign:'center', color:'#1d9bf0'}}>Twiller</h2>
      <button
        type="button"
        style={{
          width: '100%',
          background: '#fff',
          color: '#444',
          border: '1.5px solid #e6ecf0',
          borderRadius: 22,
          padding: '10px 0',
          fontWeight: 600,
          fontSize: '1.05rem',
          marginBottom: 14,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          gap: 10,
          cursor: 'pointer',
          boxShadow: '0 1px 4px rgba(29,155,240,0.07)'
        }}
        disabled
        title="Google sign-in coming soon"
      >
        <img src="https://upload.wikimedia.org/wikipedia/commons/5/53/Google_%22G%22_Logo.svg" alt="Google" style={{width:22, height:22, marginRight:8}} />
        Sign in with Google
      </button>
      <form onSubmit={handleSubmit}>
        {mode === 'signup' && (
          <input
            type="text"
            placeholder="Name"
            value={name}
            onChange={e => setName(e.target.value)}
            required
            style={{marginBottom:8}}
          />
        )}
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={e => setEmail(e.target.value)}
          required
          style={{marginBottom:8}}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={e => setPassword(e.target.value)}
          required
          style={{marginBottom:8}}
        />
        <button type="submit" disabled={loading} style={{width:'100%',marginBottom:8}}>
          {loading ? 'Please wait...' : mode === 'login' ? 'Login' : 'Sign Up'}
        </button>
      </form>
      <div style={{textAlign:'center'}}>
        {mode === 'login' ? (
          <>
            Don't have an account?{' '}
            <button onClick={()=>setMode('signup')} style={{color:'#1d9bf0',background:'none',border:'none',cursor:'pointer'}}>Sign Up</button>
          </>
        ) : (
          <>
            Already have an account?{' '}
            <button onClick={()=>setMode('login')} style={{color:'#1d9bf0',background:'none',border:'none',cursor:'pointer'}}>Login</button>
          </>
        )}
      </div>
    </div>
  );
}
