import React, {useState} from 'react';
import API from '../api';

export default function ProfileSettings({ notificationsEnabled = true, onNotificationChange }){
  const [allow, setAllow] = useState(notificationsEnabled);
  const [msg, setMsg] = useState('');

  async function toggle(){
    setMsg('');
    if (!allow) {
      const p = await Notification.requestPermission();
      if (p !== 'granted') {
        setMsg('Please allow notifications in your browser settings.');
        return;
      }
    }
    setAllow(!allow);
    if (onNotificationChange) onNotificationChange(!allow);
    setMsg('Notification preference saved!');
  }

  return (
    <div className="card" style={{border:'2px solid #1d9bf0', background:'#e8f5fd', marginBottom:24}}>
      <h3 style={{color:'#1d9bf0', marginBottom:10}}>ðŸ”” Notification Preferences</h3>
      <label style={{fontWeight:500, fontSize:'1.08em'}}>
        <input type="checkbox" checked={allow} onChange={toggle} style={{marginRight:8}}/>
        Enable browser notifications for tweets with <b>cricket</b> or <b>science</b>
      </label>
      {msg && <div style={{color: msg.includes('saved') ? '#388e3c' : '#d32f2f', marginTop:8, fontWeight:500}}>{msg}</div>}
      <div style={{fontSize:'0.97em', color:'#555', marginTop:10}}>
        You can change this anytime. Notifications help you stay updated on trending topics!
      </div>
    </div>
  );
}
