import React from "react";
import PostComposer from "../components/PostComposer";
import AudioRecorder from "../components/AudioRecorder";
import CustomVideoPlayer from "../components/CustomVideoPlayer";

export default function Feed({ tweets, fetchTweets }) {
  return (
    <main className="flex-1 px-0 md:px-8 py-6 border-x border-gray-200 min-h-screen bg-gradient-to-b from-white to-blue-50">
      <div className="max-w-xl mx-auto">
        <PostComposer onPosted={fetchTweets} />
        <AudioRecorder onUploaded={fetchTweets} />
        {/* Tweets timeline */}
        <div className="mt-6 flex flex-col gap-4">
          {tweets.map(t => (
            <div key={t._id} className="bg-white shadow-sm rounded-lg p-4 hover:bg-gray-50 transition">
              <strong>{t.user?.name}</strong>
              <p>{t.text}</p>
              {t.audioUrl && <audio controls src={`http://localhost:5000${t.audioUrl}`} />}
              {t.videoUrl && <CustomVideoPlayer src={t.videoUrl} />}
            </div>
          ))}
        </div>
      </div>
    </main>
  );
}
