import React, { useEffect, useState } from 'react';
import API from './api';
import PostComposer from './components/PostComposer';
import ProfileSettings from './components/ProfileSettings';
import AudioRecorder from './components/AudioRecorder';
import SubscriptionPage from './components/SubscriptionPage';
import CustomVideoPlayer from './components/CustomVideoPlayer';
import ForgotPassword from './components/ForgotPassword';

export default function App() {
  const [tweets, setTweets] = useState([]);
  const [notificationsEnabled, setNotificationsEnabled] = useState(() => {
    const saved = localStorage.getItem('notificationsEnabled');
    return saved === null ? true : saved === 'true';
  });

  useEffect(()=>{ fetchTweets() }, []);

  // Listen for notification setting changes from ProfileSettings
  function handleNotificationSetting(val) {
    setNotificationsEnabled(val);
    localStorage.setItem('notificationsEnabled', val);
  }

  async function fetchTweets(){
    const res = await API.get('/tweets');
    setTweets(res.data);
  }

  // Show notification for new tweets with cricket/science
  useEffect(() => {
    if (!notificationsEnabled || !('Notification' in window)) return;
    tweets.forEach(t => {
      if (!t._notified && t.text && /(cricket|science)/i.test(t.text)) {
        if (Notification.permission === 'granted') {
          new Notification('New Tweet', { body: t.text });
          t._notified = true;
        }
      }
    });
  }, [tweets, notificationsEnabled]);

  // Example user info for profile section
  const user = {
    name: 'John Doe',
    avatar: 'https://abs.twimg.com/sticky/default_profile_images/default_profile_400x400.png',
    handle: '@johndoe'
  };
  return (
    <>
      <div className="navbar">Twiller</div>
      <div className="app">
        <div className="sidebar">
          <div className="profile-section">
            <img className="profile-avatar" src={user.avatar} alt="avatar" />
            <div>
              <div style={{fontWeight:'bold'}}>{user.name}</div>
              <div style={{color:'#536471', fontSize:'0.95em'}}>{user.handle}</div>
            </div>
          </div>
          <ProfileSettings notificationsEnabled={notificationsEnabled} onNotificationChange={handleNotificationSetting} />
          <SubscriptionPage />
        </div>
        <main>
          <PostComposer onPosted={fetchTweets}/>
          <AudioRecorder onUploaded={fetchTweets}/>
          <ForgotPassword/>
          <div>
            {tweets.map(t => (
              <div key={t._id} className="tweet">
                <strong>{t.user?.name}</strong>
                <p>{t.text}</p>
                {t.audioUrl && <audio controls src={`http://localhost:5000${t.audioUrl}`} />}
                {t.videoUrl && <CustomVideoPlayer src={t.videoUrl} />}
              </div>
            ))}
          </div>
        </main>
      </div>
    </>
  );
}
