import React, { useEffect, useRef } from "react";
import PostComposer from "../components/PostComposer";
import AudioRecorder from "../components/AudioRecorder";
import TweetCard from "../components/TweetCard";
import API from "../api";

function showBrowserNotification(tweet) {
  if (Notification.permission === "granted") {
    new Notification("New Tweet", { body: tweet.text });
  }
}

export default function Feed({ tweets = [], fetchTweets }) {
  const notifiedIds = useRef(new Set());
  const [notificationsEnabled, setNotificationsEnabled] = React.useState(true);

  useEffect(() => {
    // Get user notification preference
    API.get("/auth/me").then(res => {
      setNotificationsEnabled(res.data.notificationsEnabled ?? true);
    }).catch(()=>{});
    if (Notification && Notification.permission !== "granted") {
      Notification.requestPermission();
    }
  }, []);

  useEffect(() => {
    tweets.forEach(tweet => {
      if (!tweet) return;
      // backend may attach a notify flag when posting
      const shouldNotify = tweet.notify || (tweet.text && /(cricket|science)/i.test(tweet.text));
      if (notificationsEnabled && shouldNotify && !notifiedIds.current.has(tweet._id)) {
        showBrowserNotification(tweet);
        notifiedIds.current.add(tweet._id);
      }
    });
  }, [tweets, notificationsEnabled]);

  return (
    <div className="w-full max-w-2xl mx-auto px-2 md:px-0">
      {/* Sticky top bar - improved contrast and shadow */}
      <div className="sticky top-0 z-10 bg-blue-900 border-b px-6 py-3 font-bold text-xl text-white shadow-md backdrop-blur flex items-center">
        Home
      </div>
      {/* Composer */}
      <div className="bg-white rounded-lg shadow-sm p-4 mt-4 mb-2">
        <PostComposer onPosted={fetchTweets} />
        {/* Improved Audio Tweet Card Styling */}
        <div className="bg-blue-50 rounded-xl shadow p-4 mt-4 mb-2 border border-blue-100">
          <AudioRecorder onUploaded={fetchTweets} />
        </div>
      </div>
      {/* Tweets timeline */}
      <div className="mt-4 flex flex-col gap-4">
        {tweets.map(t => (
          <TweetCard key={t._id} tweet={t} />
        ))}
      </div>
    </div>
  );
}
